       this.setState({
            name: '',
            email: ''
       })
    }

    render() {
        return (
            <div>
            <form>
                <label>
                    Name:
                    <input
                        name='name'
                        value={this.state.name}
                        onChange={e => this.handleChange(e)}/>
                </label>
                <label>
                    Email:
                    <input 
                        name='email'
                        value={this.state.email} 
                        onChange={e => this.handleChange(e)}/>
                </label>
                <button onClick={(e) => this.onSubmit(e)}>Send</button>         
            </form>
            </div>
        );
    }
}

